person.css:
html {
    overflow: initial!important;
}
sr-rd-content div {
    margin-bottom: 5px!important;
}
sr-rd-content .notion-bookmark-block div[role="button"] > div:nth-child(1) img {
    width: 16px;
    height: 16px;
    min-width: 16px;
    margin-right: 6px;
}
sr-rd-content .notion-collection-block .notion-page-block a {
    display: block;
    color: inherit;
    text-decoration: none;
    box-shadow: rgb(15 15 15 / 10%) 0px 0px 0px 1px, rgb(15 15 15 / 10%) 0px 2px 4px;
    border-radius: 3px;
    background: white;
    overflow: hidden;
    transition: background 100ms ease-out 0s;
    position: static;
    height: 100%;
    text-decoration: none!important;
    font-weight: 400!important;
    padding: 0;
}
sr-rd-content .notion-collection-block img {
    display: block;
    object-fit: cover;
    border-radius: 1px 1px 0px 0px;
    width: 100%;
    height: 113.625px;
    object-position: center 21.61%;
}
sr-rd-content .notion-record-icon {
    margin-right: 10px!important;
}
sr-rd-content .notion-record-icon img {
    display: block;
    margin-top: 12px;
    margin-right: 0;
    object-fit: cover;
    border-radius: 3px;
    width: 15.984px;
    height: 15.984px;
    transition: opacity 100ms ease-out 0s;
}
sr-rd-content .notion-text-block img {
    width: 1em;
    height: 1em;
    vertical-align: -0.15em;
    margin-right: 0.2em;
    border-radius: 1px;
} 
sr-rd-content svg { display: none; }
sr-notion-video {
    display: flex;
    justify-content: center;
    align-items: center;
}
sr-rd-content .notion-selectable.notion-video-block {
    display: flex;
    justify-content: center;
    align-items: center;
}
sr-notion-video iframe {
    position: initial!important;
    width: 100%!important;
    height: 400px!important;
}
sr-notion-video .sr-rd-content-center {
    width: 100%;
}
sr-notion-bookmark {
    display: flex;
    justify-content: center;
    margin: 16px auto;
    padding: 12px 12px 9px 12px;
    border: 3px solid #F6F6F6;
}

